func:
// Array of pseudo random temperatures
var temp1 = [15,17,18.5,20,21.5,23,24,22.2,19,32];

// Array of pseudo random relative humidities
var humidity1 = [50,55,61,68,65,60,53,49,45,47];

// Counter to select from array.
var counter1 = context.get('counter1')||0;
counter1 = counter1+1;
if(counter1 > 9) counter1 = 0;
context.set('counter1',counter1);

// Create MQTT message in JSON
msg = {
 payload: JSON.stringify(
 {
 d:{
 "temperature" : temp1[counter1],
 "humidity" : humidity1[counter1] }
 }
 )
};
return msg;

if(msg.payload.connect){
    msg.text = msg.payload.text = 'We are connected BB8 with IBM IoT Platform!';
    msg.deviceId = 'Robot';
    msg.deviceType = 'Robot';
    msg.eventCommandType = 'Run';

    msg.payload.action = '#connect';
    return msg;
}

return null;

if(msg.payload.data) {
    if(msg.payload.eSense.attention > 50 && 
    msg.payload.eSense.attention - msg.payload.eSense.meditation > 15) {
        msg.deviceId = 'Robot';
        msg.deviceType = 'Robot';
        msg.eventCommandType = 'Run';

        msg.payload.action = '#run';
        return msg;
    }
}

return null;

if(msg.payload.turn) {
    msg.deviceId = 'Robot';
    msg.deviceType = 'Robot';
    msg.eventCommandType = 'Run';

    msg.payload.action = '#turn';

    return msg;
}

return null;

if(msg.payload.data) {
    if(msg.payload.eSense.meditation - msg.payload.eSense.attention < 15 && msg.payload.eSense.meditation > 50) {
        msg.deviceId = 'Robot';
        msg.deviceType = 'Robot';
        msg.eventCommandType = 'Run';

        msg.payload.action = '#back';
        return msg;
    }

}
return null;

if(msg.payload.spin) {
    if(msg.payload.blinkStrength > 200) {
        msg.deviceId = 'Robot';
        msg.deviceType = 'Robot';
        msg.eventCommandType = 'Run';

        msg.payload.action = '#spin';
        return msg;
    }
}
return null;

if(msg.payload.data) {
    if(msg.payload.blinkStrength > 35 && msg.payload.blinkStrength < 80) {
        msg.deviceId = 'Robot';
        msg.deviceType = 'Robot';
        msg.eventCommandType = 'Run';

        msg.payload.action = '#stop';
        return msg;
    }
}

return null;

if(msg.payload.blink) {
    if(msg.payload.blinkStrength > 100) {
        msg.deviceId = 'Robot';
        msg.deviceType = 'Robot';
        msg.eventCommandType = 'Run';

        msg.payload.action = '#color';
        return msg;
    }
}

return null;

if(msg.payload.disconnect) {
    msg.deviceId = 'Robot';
    msg.deviceType = 'Robot';
    msg.eventCommandType = 'Run';

    msg.payload.action = '#disconnect';
    return msg;
}

return null;

if(msg.payload.lightOn) {
    msg.deviceId = 'Light';
    msg.deviceType = 'Robot';
    msg.eventCommandType = 'Run';

    msg.payload.action = '#on';
    return msg;
}


return null;

if(msg.payload.lightOff) {
    msg.deviceId = 'Light';
    msg.deviceType = 'Robot';
    msg.eventCommandType = 'Run';

    msg.payload.action = '#off';
    return msg;
}
return null;

if(msg.payload.arm) {
    msg.deviceId = 'Arm';
    msg.deviceType = 'Robot';
    msg.eventCommandType = 'Run';

    msg.payload.action = '#on';
    return msg;
}

return null;

if(msg.payload.car) {
    msg.deviceId = 'Car';
    msg.deviceType = 'Robot';
    msg.eventCommandType = 'Run';

    msg.payload.action = '#start-engine';
    return msg;
}

return null;
